import React, { useState } from "react";
import   "../App.css";


function CreditCard(props){
    const [defaultCardValue,setDefaultCardValue] = useState({
         cardNo: "0000 0000 0000 0000",
         name: "CARDHOLDER NAME",
         mm: "00",
         yy: "00",
         cvc: "***",
    })
    
   
    return(
    <div>
       <div className="frontCard">
            <div className="circle"></div>
            <div className="circle2"></div>
            <h1 className="card-number">{defaultCardValue.cardNo}</h1>
            <div className="cardinfo">
                <span>{defaultCardValue.name}</span>
                <span className="exp">{defaultCardValue.mm}/{defaultCardValue.yy}</span>
            </div>
       </div>
       <div className="backCard">
          <span className="cvNo">{defaultCardValue.cvc}</span>
       </div>
    </div>  
    )
}

export default CreditCard;
